const getArgv = require('./getArgv');
const getJarPath = require('./getJarPath');

describe('getArgv interface', () => {
    test('typeof getJarPath should be a function', () => {
        expect(typeof getArgv).toBe('function');
    });
    test('getArgv() should return object', () => {
        process.argv = ['ignore', 'ignore', '-key1', '-key2'];
        const argv = getArgv();
        expect(typeof argv).toBe('object');
        expect(typeof argv.length).toBe('number');
        expect(argv.length).toBe(5);
        expect(argv).toEqual(expect.arrayContaining([
            '-DGRAPHVIZ_DOT=vizjs', '-jar', getJarPath(), '-key1', '-key2'
        ]));
    });
});
