const os = require('os');
const path = require('path');
module.exports = function () {
    let jar;
    switch (os.platform()) {
        case 'win32':
            jar = path.resolve(__dirname, '../bin/win32/plantuml.jar');
            break;
        case 'darwin':
            jar = path.resolve(__dirname, '../bin/macosx/plantuml.jar');
            break;
        case 'linux':
            jar = path.resolve(__dirname, '../bin/linux/plantuml.jar');
            break;
        default:
            throw new Error(`Unsupported OS ${os.platform()}`);
    }
    return jar;
}
